const payload = {
  series: [{
    metric: 'foragents.agent.tasks_completed',
    points: [[Math.floor(Date.now() / 1000), 1]],
    tags: ['env:prod', 'agent:kai']
  }]
};

await fetch('https://api.datadoghq.com/api/v1/series', {
  method: 'POST',
  headers: {
    'DD-API-KEY': process.env.DATADOG_API_KEY!,
    'DD-APPLICATION-KEY': process.env.DATADOG_APP_KEY!,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify(payload)
});

await fetch(process.env.GRAFANA_LOKI_URL!, {
  method: 'POST',
  headers: {
    Authorization: `Bearer ${process.env.GRAFANA_TOKEN}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    streams: [{
      stream: { app: 'foragents', agent: 'ops-bot' },
      values: [[`${Date.now() * 1000000}`, 'agent cycle complete']]
    }]
  })
});

import { S3Client, PutObjectCommand } from '@aws-sdk/client-s3';

const s3 = new S3Client({ region: process.env.AWS_REGION });

await s3.send(new PutObjectCommand({
  Bucket: process.env.S3_BUCKET!,
  Key: `runs/${Date.now()}.json`,
  Body: JSON.stringify({ status: 'ok' }),
  ContentType: 'application/json'
}));

import { Client } from 'pg';

const client = new Client({ connectionString: process.env.DATABASE_URL });
await client.connect();

await client.query(
  'INSERT INTO agent_runs(agent, status, created_at) VALUES($1, $2, NOW())',
  ['ops-agent', 'success']
);

await client.end();

await fetch(process.env.SLACK_WEBHOOK_URL!, {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    text: '‚úÖ Agent run complete',
    blocks: [{ type: 'section', text: { type: 'mrkdwn', text: '*Run completed* in 42s' } }]
  })
});

import { Client, GatewayIntentBits } from 'discord.js';

const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMessages] });

client.on('ready', () => console.log('Bot online'));
client.login(process.env.DISCORD_BOT_TOKEN);

await fetch(`https://api.github.com/repos/${process.env.GH_OWNER}/${process.env.GH_REPO}/actions/workflows/deploy.yml/dispatches`, {
  method: 'POST',
  headers: {
    Authorization: `Bearer ${process.env.GITHUB_TOKEN}`,
    Accept: 'application/vnd.github+json',
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({ ref: 'main', inputs: { environment: 'production' } })
});

await fetch('https://api.vercel.com/v13/deployments', {
  method: 'POST',
  headers: {
    Authorization: `Bearer ${process.env.VERCEL_TOKEN}`,
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    project: process.env.VERCEL_PROJECT_ID,
    target: 'preview',
    gitSource: { type: 'github', repo: 'reflectt/foragents.dev', ref: 'main' }
  })
});

import * as Sentry from '@sentry/node';

Sentry.init({
  dsn: process.env.SENTRY_DSN,
  environment: process.env.NODE_ENV
});

try {
  throw new Error('agent test failure');
} catch (err) {
  Sentry.captureException(err);
}

